SELECT count( 1 ) AS `count`, su.realname, mu.owner_user_id AS ownerUserId, sd.NAME AS deptName FROM member_user AS mu LEFT JOIN sys_user AS su ON su.biz_id = mu.owner_user_id LEFT JOIN sys_dept AS sd ON sd.biz_id = su.dept_id WHERE mu.deleted =FALSE AND su.deleted =FALSE AND sd.deleted =FALSE AND mu.owner_user_id in
#{id}
AND DATE_FORMAT(mu.create_time,#{time.sqlDateFormat}) between #{time.beginTime} and #{time.finalTime} GROUP BY mu.owner_user_id ,su.realname,sd.`name` ORDER BY count DESC

